// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IToken is IERC20{
    function mint(address to, uint amount) external returns(bool);
    function burn(address from, uint amount) external returns(bool);
}

interface IMB {
    function mint(address to, uint amount) external returns(bool);
    function burn(address from, uint amount) external returns(bool);
}

contracts/ProposalVote.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract ProposalVote {
    using SafeMath for uint256;

    mapping(address => uint256) public threshold;

    mapping(bytes32 => bool) isFinished;
    mapping(bytes32 => mapping(address => bool)) isVoted;
    mapping(bytes32 => uint256) counter;

    event ProposalVoted(
        address token,
        address from,
        address to,
        uint256 amount,
        address proposer,
        uint256 count,
        uint256 threshold
    );

    event ThresholdChanged(address token, uint256 oldThreshold, uint256 newThreshold);

    function _setThreshold(address token, uint256 _threshold) internal virtual {
        uint256 oldThreshold = threshold[token];
        threshold[token] = _threshold;
        emit ThresholdChanged(token, oldThreshold, _threshold);
    }

    function _vote(
        address tokenTo,
        address from,
        address to,
        uint256 amount,
        string memory txid
    ) internal virtual returns (bool result) {
        require(threshold[tokenTo] > 0, "ProposalVote: threshold should be greater than 0");
        uint256 count = threshold[tokenTo];
        bytes32 mid = keccak256(abi.encodePacked(tokenTo, from, to, amount, txid));
        require(isFinished[mid] == false, "_vote::proposal finished");
        require(isVoted[mid][msg.sender] == false, "_vote::msg.sender voted");
        counter[mid] = counter[mid].add(1);
        isVoted[mid][msg.sender] = true;

        if (counter[mid] >= count) {
            isFinished[mid] = true;
            result = true;
        }

        emit ProposalVoted(tokenTo, from, to, amount, msg.sender, counter[mid], count);
    }
}

contracts/Toll.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "./lib/SafeDecimalMath.sol";

contract Toll {
    using SafeDecimalMath for uint256;
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    // fee
    // leave ethereum
    mapping(address => uint256) public lockFeeRatio;
    mapping(address => uint256) public lockFeeAmount;
    // back ethereum
    mapping(address => uint256) public unlockFeeRatio;
    mapping(address => uint256) public unlockFeeAmount;

    mapping(address => EnumerableSetUpgradeable.AddressSet) internal feeToSet;

    event FeeChange(
        address token,
        uint256 lockFeeAmount,
        uint256 lockFeeRatio,
        uint256 unlockFeeAmount,
        uint256 unlockFeeRatio
    );

    event FeeToRemoved(address token, address account);

    event FeeToAdded(address token, address account);

    function _setFee(
        address token,
        uint256 _lockFeeAmount,
        uint256 _lockFeeRatio,
        uint256 _unlockFeeAmount,
        uint256 _unlockFeeRatio
    ) internal virtual {
        require(_lockFeeRatio <= 1e18, "fee ratio not correct");
        require(_unlockFeeRatio <= 1e18, "fee ratio not correct");

        lockFeeAmount[token] = _lockFeeAmount;
        lockFeeRatio[token] = _lockFeeRatio;
        unlockFeeAmount[token] = _unlockFeeAmount;
        unlockFeeRatio[token] = _unlockFeeRatio;
        emit FeeChange(token, _lockFeeAmount, _lockFeeRatio, _unlockFeeAmount, _unlockFeeRatio);
    }

    function _addFeeTo(address token, address account) internal virtual {
        require(feeToSet[token].contains(account) == false, "Toll::account was feeTo already");
        feeToSet[token].add(account);
        emit FeeToAdded(token, account);
    }

    function _removeFeeTo(address token, address account) internal virtual {
        require(feeToSet[token].contains(account) == true, "Toll::account is not feeTo");
        feeToSet[token].remove(account);
        emit FeeToRemoved(token, account);
    }

    function calculateFee(
        address token,
        uint256 amount,
        uint256 crossType
    ) public view virtual returns (uint256 feeAmount, uint256 remainAmount) {
        uint256 _feeMinAmount;
        uint256 _feeRatio;
        if (crossType == 0) {
            // leave ethereum
            _feeMinAmount = lockFeeAmount[token];
            _feeRatio = lockFeeRatio[token];
        } else {
            // back ethereum
            _feeMinAmount = unlockFeeAmount[token];
            _feeRatio = unlockFeeRatio[token];
        }
        feeAmount = _feeMinAmount + amount.multiplyDecimal(_feeRatio);
        remainAmount = amount - feeAmount;
    }

    function feeToLength(address token) public view returns (uint256 len) {
        len = feeToSet[token].length();
    }

    function getFeeTo(address token, uint256 i) public view returns (address account) {
        account = feeToSet[token].at(i);
    }
}

